package week1.day2;

public class ConverttoPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int s = -40;
		int c = 0;
		if (s<0) {
			System.out.println(s + " is a negative number");
			c = (-2*s)+s;
			System.out.println("The positive number of "+s+" is " +c);
			
		}else {
			System.out.println(s + " is a positive number");
		}
	}
	}


